mapper.select:
select count(*)
from noteLike
where noteId = #{noteId}
  and userId = #{userId};

mapper.update:
UPDATE note
SET category=#{category},
    noteTitle=#{noteTitle},
    note=#{note}
WHERE noteId = #{noteId}
  AND userId = #{userId}

UPDATE note
SET noteLike=noteLike + 1
WHERE noteId = #{noteId}

UPDATE note
SET noteLike=noteLike - 1
WHERE noteId = #{noteId}

UPDATE note
SET noteCommentNum=noteCommentNum + 1
WHERE noteId = #{noteId}

UPDATE note
SET noteCommentNum=noteCommentNum - 1
WHERE noteId = #{noteId}

UPDATE note
SET pageViewState = 'DELETE'
WHERE noteId = #{noteId}
  AND userId = #{userId}

mapper.delete:
delete
from noteLike
where noteId = #{noteId}
  and userId = #{userId};
